import React from 'react'
import * as Axios from 'axios'

class Location extends React.Component {


    
constructor(props){
    super(props);
    this.getmethod = this.getmethod.bind(this);
    this.locate = this.locate.bind(this);
    this.state = {lat: 'banana', long: 'apple'};
    // this.lat = this.lat.bind(this);
    // this.long = this.long.bind(this);
}

locate () {

    const posStatus = document.querySelector('#posStatus');
    const locInfo = document.querySelector('#locInfo');
    posStatus.innerHTML='Locating...'
        if (navigator.geolocation) {
            console.log(this);
            navigator.geolocation.getCurrentPosition(this.getGeoLocation);
              
            this.getmethod();
          
        }

        
    }

    render() {

    return(

        <div>
        <button onClick={this.locate}>Get Location</button>
        <h1 id = 'posStatus'></h1>
        <a id = 'locInfo' target="#"></a>
        </div>

    )

}

getmethod () {

    Axios.get(`https://localhost:8080/location?lat=${this.state.lat}&long=${this.state.long}`
        )
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
}


getGeoLocation = (position) => {
    this.setState({lat : position.coords.latitude, long : position.coords.longitude});
    this.setState((state, props) => {
        return {counter: state.counter + props.step};
      });
    //posStatus.innerHTML='Location';
    //locInfo.innerHTML = `Latitude: ${lat}, Longitude: ${long}`;
}

}

export default Location
